key.syntax:
if (key === 'a') {
    // 'a'キーが押された時の処理
}

beginShape_endShape.syntax:
beginShape();
vertex(20, 20);
vertex(40, 20);
vertex(40, 40);
endShape(CLOSE); // CLOSEで図形を閉じる

push_pop.syntax:
push();
translate(50, 50);
fill(255, 0, 0);
rect(0, 0, 30, 30);
pop(); // 設定が元に戻る

noLoop.syntax:
function setup() {
    createCanvas(400, 400);
    noLoop(); // draw関数は一度しか実行されない
    background(220);
    ellipse(width/2, height/2, 50, 50);
}

mousePressed.syntax:
function mousePressed() {
    console.log("マウスがクリックされました！");
}

loadPixels_updatePixels.syntax:
loadPixels();
pixels[0] = 255; // 左上のピクセルの赤成分を最大に
updatePixels();

hue_saturation_brightness.syntax:
let myColor = color(255, 0, 0); // 赤色
let h = hue(myColor);
let s = saturation(myColor);
let b = brightness(myColor);

keyisdown.syntax:
if (keyIsDown(LEFT_ARROW)) {
    // 左矢印キーが押されている間の処理
}

variables.syntax:
let score = 100; // 数字をしまう
const name = "Pico"; // 文字をしまう (変えられない)
score = 120; // 後から値を変えることもできる

functions.syntax:
function greet(name) {
    return "こんにちは、" + name + "さん！";
}
let message = greet("Codey");
console.log(message);

if_else.syntax:
let score = 80;
if (score > 70) {
    console.log("合格！");
} else {
    console.log("もう少しがんばろう！");
}

let weather = "晴れ";
if (weather === "晴れ") {
    console.log("公園に行こう！");
} else if (weather === "雨") {
    console.log("家で遊ぼう。");
} else {
    console.log("何しようかな？");
}

loops.syntax:
for (let i = 0; i < 5; i++) {
    console.log(i + "回目！");
}

let count = 0;
while (count < 3) {
    console.log("まだ" + count + "回目");
    count = count + 1; // countを増やすのを忘れないで！
}

operators.syntax:
// 算術演算子
let a = 10;
let b = 3;
console.log(a + b); // 足し算: 13
console.log(a - b); // 引き算: 7
console.log(a * b); // 掛け算: 30
console.log(a / b); // 割り算: 3.33...
console.log(a % b); // 余り: 1

// 比較演算子
console.log(a === 10); // 等しいか: true
console.log(a !== b); // 等しくないか: true
console.log(a > b);   // より大きいか: true
console.log(a < b);   // より小さいか: false
console.log(a >= 10); // 以上か: true
console.log(a <= b); // 以下か: false

// 論理演算子
let isSunny = true;
let isWarm = false;
console.log(isSunny && isWarm); // かつ (両方trueならtrue): false
console.log(isSunny || isWarm); // または (どちらかtrueならtrue): true
console.log(!isSunny);          // ではない (trueならfalse、falseならtrue): false

class.syntax:
class MyObject {
    constructor(x, y) { // ものが作られるときに最初に呼ばれる
        this.x = x;
        this.y = y;
    }

    update() { // ものの状態を更新する
        this.x += 1;
    }

    show() { // ものを描画する
        ellipse(this.x, this.y, 10, 10);
    }
}

normalMaterial.syntax:
function setup() {
    createCanvas(400, 400, WEBGL);
}

function draw() {
    background(200);
    normalMaterial(); // 法線マテリアルを適用
    rotateX(frameCount * 0.01);
    rotateY(frameCount * 0.005);
    box(80);
}

TRIANGLE_STRIP.syntax:
beginShape(TRIANGLE_STRIP);
vertex(x1, y1, z1);
vertex(x2, y2, z2);
// ... 続けてvertex()で頂点を追加 ...
endShape();
